        <label>Email
            <input type="text" name="email" [(ngModel)]="user.email" required>
        </label>

        <label> Password
            <input type="text" name="password" [(ngModel)]="user.password" required>
        </label>

        <div class="button_form_container">
            <button class="button_form">
                Submit
            </button>
        </div>

</form>
