import { Component } from '@angular/core';
import { Book } from 'src/app/models/book';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css']
})
export class AddBookComponent {

  public books: Book[];

  constructor(){

    this.books = [

      new Book(1200007, 40040, "Dejar ir", "Autoayuda", "Dr. David Hawkins", 19.99, "https://imagessl4.casadellibro.com/a/l/t7/14/9788494248214.jpg"),

      new Book(4170002, 3031, "Los 5 niveles de apego", "Autoayuda", "Miguel Ruiz Jr", 14.99, "https://imagessl7.casadellibro.com/a/l/t7/77/9788479538477.jpg"),

      new Book(4000441, 4100, "Titanes", "Liderazgo", "Tim Ferriss", 23.99, "https://images.cdn2.buscalibre.com/fit-in/360x360/39/85/3985f893d89905b8495825bbfac2239b.jpg"),

      new Book(1000222, 4109, "Encantado de conocerme", "Autoayuda", "Borja Vilaseca", 12.99, "https://imagessl6.casadellibro.com/a/l/t7/26/9788466361026.jpg"),

    ]

  }

        addBook(id_user:number, id_book:number, title:string, type:string, author:string, price:number, photo:string) {


        let newBook = new Book(id_user, id_book, title, type, author,price, photo);
        this.books.push(newBook);
  }

  deleteBook(selectBook:string):void {

    this.books = this.books.filter(book => book.title !== selectBook)

  }

}




<form class="form_container">
    <h1>
        new <br> BOOK
    </h1>
    <div>
        <div class="form_child">
            <div class="form_second_child">
                <br> <br> <br> <br>
                <br><input type="number" placeholder="id user *" #id_user> <br>
                <br><input type="number" placeholder="id book *"  #id_book> <br>
                <br><input type="text" placeholder="Title" #title> <br>
                <br> <input type="text" placeholder="Type" #type> <br>
                <br> <input type="text" placeholder="Author" #author> <br>
                <br><input type="number" placeholder="Price" #price> <br>
                <br> <input type="url" placeholder="Photo url" #photo>
                <div class="button_div">
                    <button type="reset" class="form_buttom" (click)="addBook(id_user.value, id_book.value, title.value, type.value, author.value, price.value, photo.value)">

                        add book
                    </button>

                </div>
            </div>
                <div class="manyBooks_container">
                    <img src="/assets/img/manyBooks.jpeg" alt="" class="manyBooks">
                </div>
        </div>
    </div>
</form>


