import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Book } from 'src/app/models/book';
import { BooksService } from 'src/app/shared/books.service';

@Component({
  selector: 'app-update-book',
  templateUrl: './update-book.component.html',
  styleUrls: ['./update-book.component.css']
})
export class UpdateBookComponent {

  public books: Book;
  // public bookChange: Book[];

  constructor(public BookService: BooksService, public router: Router) {
    // this.books = this.BookService.getAll()
  }


  modifyBook(id_user: number, id_book: number , title:string, type:string, author:string, price:number, photo:string) {


    this.books = new Book (id_user, id_book, title, type, author, price, photo)

    if(this.BookService.edit(this.books) == true) {
      console.log("Modificado con éxito")

    } else {
      console.log("El libro no pudo ser modificado")
    }
  }

}
