import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, NgForm, Validators } from '@angular/forms';
import { User } from 'src/app/models/user';

@Component({
  selector: 'app-form-login',
  templateUrl: './form-login.component.html',
  styleUrls: ['./form-login.component.css']
})
export class FormLoginComponent {

  public myForm : FormGroup;

  constructor( private formBuilder: FormBuilder) {

    this.buildForm();
  }

    private buildForm() {

      const minPassLenght = 8;

      this.myForm = this.formBuilder.group({

        'email': [, [Validators.required, Validators.email]],
        'password': [, [Validators.required, Validators.minLength(minPassLenght)]],


      })
      }

      ngOnInit(): void{

      }

      onSubmit() {  
        console.log('Email:', this.myForm.get('email').value);
        console.log('Password:', this.myForm.get('password').value);
      }
  }


<form [formGroup]="myForm" class="container" (ngSubmit)="onSubmit()">



        <label>Email
            <input type="email" class="form-control" name="email" [(ngModel)]="user.email" required
            [ngClass]="{'is-invalid': (myForm.get(email).dirty || myForm.get('email').touched) && !myForm.get('email').valid}">
            <div *ngIf="!myForm.get('email').valid" class="invalid-feedback">
                Email is neccesary **
            </div>

        </label>


        <label> Password
            <input type="password" class="form-control" name="password" [(ngModel)]="user.password" required
            [ngClass]="{'is-invalid': (myForm.get(password).dirty || myForm.get('password').touched) && !myForm.get('password').valid}">
            <div *ngIf="!myForm.get('password').valid" class="invalid-feedback">
                <div ngIf="myForm.get('password').errors.required"></div>password is necessary **
                <div *ngIf="myForm.get('password').errors.minlenght">password has at least 8 characters ** </div>
            </div>
        </label>



        <div class="button_form_container">
            <button class="button_form">
                Submit
            </button>
        </div>

</form>
