import { Component } from '@angular/core';
import { User } from 'src/app/models/user';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})


export class ProfileComponent {

  public user:User;

  constructor(){

    this.user = new User(127, "Ivana", "Pizzo Logioio", "ivb.pizzo@gmail.com", "", "https://media.licdn.com/dms/image/D4D03AQHJsuZ6Z5SrHw/profile-displayphoto-shrink_800_800/0/1679954552478?e=2147483647&v=beta&t=5DGJjcJuMLUDT0ORobbfIDepf_ByOjd1KK_NQlCvi_8")


  }

  public showName(name:string) {

    this.user.name = name;
  }


  public changeUser(name:string, first_last_name:string, email:string, photo:string) {

    this.user.name = name;
    this.user.first_last_name = first_last_name;
    this.user.email = email; 
    this.user.photo = photo;

  }

}



<div class="container">

    <div class="child_1">
            <img src="{{user.photo}}" alt="" class="profile_picture">
            <div class="child_1_title">
                    {{user.name}}
                    {{user.first_last_name}}
            </div>
        <div class="child_1_inside">
            {{user.email}} <br>
        </div>
    </div>

    <div>

    </div>

    <div class="child_2_title">
        Editar datos:
    </div>
    <div class="child_2">
        <div class="child_2_inside">
            Nombre <input #name type="text" class="input_1"> <br>

            Apellidos <input #first_last_name type="text" class="input_1"> <br>

            Correo <input #email type="text" class="input_1"> <br>

            Photo <input #photo type="text" class="input_1"> <br>

            <div class="submit_buttom_container"> <br> <br> <br>
                <button class="submit_buttom" type="button" (click)="changeUser(name.value,first_last_name.value, email.value, photo.value)"> Modificar </button>
            </div>
        </div>
    </div>

</div>
