import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Book } from 'src/app/models/book';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {

  @Input() book: Book;
  @Input() par: boolean;
  @Output() selectBook = new EventEmitter<number>();

  deleteBook():void{
    this.selectBook.emit(this.book.id_book);
  }
}

<!-- COMPONENTE HIJO -->

    <div class="container_books">
        <div class="card-group">
            <div class="card" style="width: 16rem;" [ngClass]=" par ? 'bg-light' : 'bg-light'">
                <div class="button_even_x_container">
                    <button class="button_even_x" type="button" (click)="deleteBook()">  x </button>     
                </div>
                <img src="{{book.photo}}" class="card-img-top" alt="...">
                    <div class="card-body">
                        <p class="card-title">{{book.title}}</p>
                    </div>
                        <ul class="list-group">
                            <li class="list-group">{{book.author}}</li>
                            <li class="list-group">{{book.price}} €</li>
                            <li class="list-group">{{book.type}}</li>
                        </ul>
                        <div class="card-link"> {{book.id_book| pipeBook}} </div>
                        <br>
                    <button class="button_even" type="button" [ngClass]=" par ? 'bg-light' : ''" > Buy</button> 

            </div>    
        </div>    
    </div>
